    @staticmethod
    def is_contains_emoji(text: str) -> bool:
        return text in StringUtils.UNICODE_EMOJI
    
    @staticmethod
    def regex_extract(string: str, pattern: str) -> List[str]:
        matches: List[str] = re.findall(pattern=pattern, string=string)
        return matches
    
    @staticmethod
    def regex_match(string: str, pattern: str) -> str:
        match = re.match(pattern=pattern, string=string)
        if match:
            return match.group()
        else:
            return ''
